    //     setMenuState(false)
    //   } else if (ctx.width > ) {
    //     setMenuState(true)
    //   }
    // }, [ctx.width])

    const NavBar = () =>{
      return (
        <ul className={`navItems`}>
          <li className="navLink"ß>
            <Link to="/">Home</Link>
          </li>
          <li className="navLink">
            <Link to="/menu">Menu</Link>
          </li>
        </ul>
      );
    }

    return (
      <div>
        <Router>
          <div className="wholeBar">
            <div className="navDiv">
              <div>
                <p></p>
              </div>
              <Toggle onClick={setMenuState} currentMenu={menuState}/>
              <NavBar />
            </div>
          </div>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route exact path="/menu">
              <Menu />
            </Route>
          </Switch>
        </Router>
      </div>
    );
}

export default Body
