import React, {useState} from 'react'
import './MenuItem.css'
import DietType from './DietType';
//import Choices from './Choices';

const MenuItem = props => {
    const [choice, setChoice] = useState(props.price === 'choice')
    if (choice === true){
      console.log(props.choices)
    }
    return (
      <div className="menuItem">
        <div>
          <p>{props.itemNum}.</p>
        </div>
        <div>
          <h3 className="itemHead">{props.name}</h3>
          <p className="itemBody">{props.description}</p>
          <DietType type={props.dietType} />
          { choice && 
          <ul>
            {props.choices.map(choice =>
              <li key={choice.id}>{choice.meat} </li>)}
          </ul>
          }
        </div>
        <div>
          <h3 className="itemPrice">$ {props.price}</h3>
        </div>
      </div>
    );
}

export default MenuItem
